	public function fetch_country() {
		$url      = 'https://restcountries.com/v3.1/all';
		$response = wp_remote_get( $url );
		$body     = wp_remote_retrieve_body( $response );
		$data     = json_decode( $body );
		return $data;
	}



	public function country_list() {
		// get form public api
		return json_decode( $this->country, true );
	}

	// get currency list
	public function currency_list() {
		
		$currency_list =  json_decode( $this->currency, true );

		// sort currency list
		// example  array array( name => 'ALL', value => 'ALL' )

		$currency = array();
		foreach ( $currency_list as $key => $value ) {
			$currency[] = array( 'name' => $key, 'value' => $key );
		}

		return $currency;
	}
